package com.hjp123.lei.service.edu.controller.oos;import com.hjp123.lei.common.code.R;import com.hjp123.lei.common.oos.seriver.FileService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.multipart.MultipartFile;/** * @description: 上传接口 * @author: Hjp * @time: 2020/7/22 15:09 */@RestController@RequestMapping("/edu/oss")@CrossOriginpublic class OssController {    @Autowired    private FileService fileService;    @PostMapping("/upload")    public R uploadOssFile(MultipartFile file){        /**         *         *         * @description: 上传图片接口         * @param file 上传的文件         * @return: com.hjp123.commonutils.R 统一返回接口         * @author: Hjp         * @time: 2020/7/23 14:11         */        String url = fileService.uploadAvatar(file);        return R.ok().data("url",url).message("图片上传成功");    }}